import './NewsPanel.css';
import React, { useState, useEffect, useCallback } from 'react';
import "./NewsPanel.css";
import NewsCard from '../NewsCard/NewsCard';
import Auth from '../Auth/Auth';
import { CircularProgress, List, Box } from '@mui/material';
import { debounce } from 'lodash';

const NewsPanel = () => {
  const [news, setNews] = useState(null);
  const [pageNum, setPageNum] = useState(1);
  const [loadedAll, setLoadedAll] = useState(false);

  const loadMoreNews = useCallback(() => {
    if (loadedAll) return;

    const request = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${Auth.getToken()}`
      },
      body: JSON.stringify({
        jsonrpc: '2.0',
        method: 'get_news_summaries_for_user',
        params: [Auth.getEmail(), pageNum],
        id: 1
      })
    };

    fetch('/news', request)
      .then((res) => res.json())
      .then((response) => {
        const newsResult = response.result;
        console.log(newsResult);
        if (!newsResult || newsResult.length === 0) {
          setLoadedAll(true);
          return;
        }

        setNews(prevNews => prevNews ? [...prevNews, ...newsResult] : newsResult);
        setPageNum(prev => prev + 1);
      })
      .catch(error => {
        console.error('Error loading news:', error);
      });
  }, [pageNum, loadedAll]);

  useEffect(() => {
    loadMoreNews();
    const debouncedLoadMore = debounce(loadMoreNews, 1000);
    window.addEventListener('scroll', debouncedLoadMore);
    return () => window.removeEventListener('scroll', debouncedLoadMore);
  }, [loadMoreNews]);

  const handleScroll = () => {
    const scrollY = window.scrollY || window.pageYOffset || document.documentElement.scrollTop;
    if ((window.innerHeight + scrollY) >= document.body.offsetHeight - 50) {
      loadMoreNews();
    }
  };

  if (!news) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <List sx={{ width: '100%', p: 0 }}>
      {news.map((newsItem, index) => (
        <NewsCard key={newsItem.digest || index} news={newsItem} />
      ))}
    </List>
  );
};

export default NewsPanel;
